import "./about.css";

export default function About(props){
    window.addEventListener('scroll',()=>{
        let content = document.querySelector('.about');
        let left = document.querySelector('.aboutImg');
        let right = document.querySelector('.aboutContent');
        let contentPosition = content.getBoundingClientRect().top;
        let screenPosition = window.innerHeight;

        if(contentPosition < screenPosition){
            left.classList.add('activeLeft');
            right.classList.add('activeRight');
        }else{
            left.classList.remove('activeLeft');
            right.classList.remove('activeRight');
        }
    })
    return(
        <div className="about">
          <div className="aboutContainer">
            <div className="aboutImg">
                <img src={props.aboutImg}/>
            </div>
            <div className="aboutContent">
                <div className="aboutTitle">About</div>
                    <p className="aboutBody">
                        At New Paradigm Academy, we are dedicated to creating a nurturing, stimulating and welcoming environment
                        for all children. Our unique programs incorporate a variety of educational theories, while always keeping the childâ€™s 
                        best interest in mind and allowing for hands-on, experiential learning. To find out more about us, keep reading or feel 
                        free to get in touch.
                    </p>
            </div>
          </div>
        </div>  
    );
}
