import "./write.css";
// import writeImg from "../../img/5.jpg"; 
import { useContext, useState } from "react";
import { Context } from "../../context/Context";
import axios from "axios";

export default function Write(){
    const [title,setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [file, setFile] = useState(null);
    const {user} = useContext(Context);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newPost = {
            username:user.username,
            title,
            desc,
        };
        if(file){
            const data = new FormData();
            const filename = Date.now() + file.name;
            data.append("name", filename);
            data.append("file", file);
            newPost.photo = filename;

            try{
                await axios.post("/upload", data);
            }catch(err){
                console.log(err);
            }
        }
        try{
            const res = await axios.post("/posts",newPost);
            window.location.replace("/post/" + res.data._id);
        }catch(err){
            console.log(err);
        }
    }
    return(
        <div className="writing">
            <div className="write">
            {file &&(
            <img src={URL.createObjectURL(file)} className="writeImg" alt=""/>
            )}
            <form className="writeForm" onSubmit={handleSubmit}>
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                            <i className="writeIcon fas fa-plus"></i>
                    </label>
                    <input 
                        type="file" 
                        id="fileInput" 
                        style={{display:"none"}} 
                        onChange={e=> setFile(e.target.files[0])}
                    />

                    <input 
                        type="text" 
                        placeholder="Title" 
                        className="writeInput" autoFocus={true}
                        onChange={e=>setTitle(e.target.value)}
                    />
                </div>
                <div className="writeFormGroup">
                    <textarea 
                        placeholder="Tell your story...."
                        type="text"
                        className="writeInput writeText"
                        onChange={e=>setDesc(e.target.value)}
                    >    
                    </textarea>
                </div>
                    <button className="writeSubmit" type="submit">Publish To Blog</button>
                    {/* <button className="writeSubmit">Publish To Anouncement</button> */}
            </form>
            </div>

            <div className="Annoucemnt">
            {file &&(
            <img src={URL.createObjectURL(file)} className="writeImg" alt=""/>
            )}
            <form className="writeForm" onSubmit={handleSubmit}>
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                            <i className="writeIcon fas fa-plus"></i>
                    </label>
                    <input 
                        type="file" 
                        id="fileInput" 
                        style={{display:"none"}} 
                        onChange={e=> setFile(e.target.files[0])}
                    />

                    <input 
                        type="text" 
                        placeholder="Title" 
                        className="writeInput" autoFocus={true}
                        onChange={e=>setTitle(e.target.value)}
                    />
                </div>
                <div className="writeFormGroup">
                    <textarea 
                        placeholder="Tell your story...."
                        type="text"
                        className="writeInput writeText"
                        onChange={e=>setDesc(e.target.value)}
                    >    
                    </textarea>
                </div>
                    <button className="writeSubmit" type="submit">Publish To Annoucement</button>
                    {/* <button className="writeSubmit">Publish To Anouncement</button> */}
            </form>
        </div>
        </div>
    );
}
