import "./write.css";
import writeImg from "../../img/5.jpg";

export default function Write(){
    const [title,setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [file, setFile] = useState(null);
    return(
        <div className="write">
            <img src={writeImg} className="writeImg" alt=""/>
            <form className="writeForm">
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                            <i className="writeIcon fas fa-plus"></i>
                    </label>
                    <input type="file" id="fileInput" style={{display:"none"}}/>
                    <input type="text" placeholder="Title" className="writeInput" autoFocus={true}/>
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder="Tell ypur story...."
                    type="text"
                    className="writeInput writeText">
                    </textarea>
                </div>
                    <button className="writeSubmit">Publish To Blog</button>
                    {/* <button className="writeSubmit">Publish To Anouncement</button> */}
            </form>
        </div>
    );
}
